import hacker from "../media/hacker.jpg"

const FOLLOW = "FOLLOW";
const UNFOLLOW = "UNFOLLOW";
const SET_USERS = "SET_USERS";

const initialState = {
    users: [
        {id: 1,icon:hacker, followed:true ,fullName: "Serg", status: "programmer_without_work(yet)", location:{city: "London", country: "GB"}},
        {id: 2,icon:hacker, followed:true ,fullName: "Daniel", status: "scripi...man)))", location:{city: "San-Diego", country: "USA"}},
        {id: 3,icon:hacker, followed: false ,fullName: "Max", status: "i'm wanna be fit", location:{city: "Copenhagen", country: "Denmark"}}
    ]
}

const usersReducer = (state = initialState, action) => {
    switch (action.type){
        case FOLLOW:
            return {
                ...state,
                users: state.users.map((user) => {
                    if (user.id === action.userId){
                        return {...user,followed:true}
                    }
                    return(
                    user
                    )
                })

            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map((user) => {
                    if (user.id === action.userId){
                        return {...user,followed:false}
                    }
                    return(
                        user
                    )
                })

            }
        case SET_USERS:
            return{
                ...state, users: [...state.users, ...action.users]
            }
        default:
            return state;
    }
}

export const followActionCreator = (userId) => {
    return {
        type: FOLLOW,
        userId: userId
    }
}

export const unfollowActionCreator = (userId) => {
    return {
        type: UNFOLLOW,
        userId: userId
    }
}

export const setUsersActionCreator = (users) => {
    return {
        type: SET_USERS,
        users: users
    }
}

export default usersReducer;
