import * as axios from "axios";

let baseUrl = "https://social-network.samuraijs.com/api/1.0/";

const instance = axios.create({
    withCredentials:true,
    headers: {
        "API-KEY": "858c15c3-c7f7-4ff2-8f05-b2146fd62275"
    }
});

export const getUsers = (currentPage = 2,pageSize = 10) => {
    return (
        axios.get(`${baseUrl}users?page=${currentPage}&count=${pageSize}`,
            {
                withCredentials: true
            })
            .then(response => {
            return response.data
            })

    )
}

export const followUsers = (userId) => {
    return (
        axios.get(`${baseUrl}${userId}`,
            {
                withCredentials: true
            })
            .then(response => {
                return response.data
            })

    )
}
