import React from "react";
import classes from "./MyPosts.module.css";
import anonymous from "../../../media/create_account_2_anonymous.png"
import Post from "./Post/Post";


const MyPosts = (props) => {

    let postElements = props.allPosts.map((post) =>
        <Post message={post.message} likeCounts={post.likesCount}/>
    )

    let newPostElement = React.createRef();

    let onChange = (event) => {
        props.allPosts.message = event;
        console.log(props.allPosts.message)
    }

    let addPost = () => {
        let text = newPostElement.current.value;
        props.addPost(text);
        newPostElement.current.value = "";
    }


    return (
        <div>
            {postElements}
            <div>
                <textarea ref={newPostElement} onChange={onChange}></textarea>
                <button onClick={addPost}>Add post</button>
                <button>Remove</button>
            </div>
            <div className={classes.posts}>
             </div>
        </div>
    )
}

export default MyPosts;
