import React from "react";
import Profile from "./Profile";
import {connect} from "react-redux";
import {setUserProfileActionCreator, setUsersProfileThunkCreator} from "../../redux/profileReducer";
import {Redirect, withRouter} from "react-router-dom";
import {usersAPI} from "../../api/api";
import {WithAuthRedirect} from "../../hoc/withAuthRedirect";
import {compose} from "redux";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId
        if (!userId){
            userId = 2;
        }
        this.props.setUsersProfileThunkCreator(userId);
        this.props.getUsersStatus()

    }

    render() {

        return (
            <Profile {...this.props}/>
        )
    }
}

// let AuthRedirectComponent = WithAuthRedirect(ProfileContainer);

let mapStateToProps = (state) => ({
    profile: state.profilePage.profile
})

export default compose(
    connect(mapStateToProps,{setUserProfileActionCreator, setUsersProfileThunkCreator}),
    withRouter,
    WithAuthRedirect
)(ProfileContainer)
