import * as axios from "axios";

export const getUsers = (currentPage) => {
    return (
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${this.props.pageSize}`,
            {
                withCredentials: true
            })
    )
}
