import React from "react";
import profileReducer from "./profileReducer";

export const ADD_POST = "ADD_POST";
export const UPDATE_NEW_POST_TEXT = "UPDATE_NEW_POST_TEXT";
export const UPDATE_NEW_MESSAGE_TEXT = "UPDATE_NEW_MESSAGE_TEXT";
export const SEND_MESSAGE = "SEND_MESSAGE";

let store = {

    _state :{
        profilePage: {
            posts: [
                {id: 1, message: "Hi,from Java man", likesCount: 25},
                {id: 1, message: "Python the best_ ho-ho-ho", likesCount: 55}
            ],
            newPostText: ""
        },
        messagesPage: {
            dialogs: [
                {id: 1, name: "Roman"},
                {id: 2, name: "Daniel"},
                {id: 3, name: "Max"}],
            messages: [
                {id: 1, message: "Hi"},
                {id: 2, message: "When will u connect Mongo Homie???)))"},
                {id: 3, message: "AHAHAHAHhHH"}],
            newMessageText: ""
        },
        sidebar: {
            friends: [
                {id: 1, name: "Max"},
                {id: 2, name: "Daniel"}
            ]
        }
    },

    _callSubscriber(){
        console.log("state is changed")
    },

    getState(){
         return this._state;
    },

    addPost(){
        let newPost = {
            id: 5,
            message: this._state.profilePage.newPostText,
            likesCount: 100
        };
        this._state.profilePage.posts.push(newPost);
        this._state.profilePage.newPostText = "";
        this._callSubscriber(this._state);
    },

    updateNewPostText(newText){
        this._state.profilePage.newPostText = newText;
        this._callSubscriber(this._state);
    },

    subscribe(observer){
        this._callSubscriber = observer; // observer - наблюдатель (pattern)
        //examples: addEventListener, onClick
    },
    dispatch(action){

        profileReducer(this._state)

        if(action.type === ADD_POST){
            let newPost = {
                id: 5,
                message: this._state.profilePage.newPostText,
                likesCount: 100
            };
            this._state.profilePage.posts.push(newPost);
            this._state.profilePage.newPostText = "";
            this._callSubscriber(this._state);
        }else if (action.type === UPDATE_NEW_POST_TEXT){
            this._state.profilePage.newPostText = action.newText;
            this._callSubscriber(this._state);
        }else if(action.type === UPDATE_NEW_MESSAGE_TEXT){
            this._state.messagesPage.newMessageText = action.newText;
            this._callSubscriber(this._state);
        } else if (action.type === SEND_MESSAGE) {
            let body = this._state.messagesPage.newMessageText;
            this._state.messagesPage.newMessageText = '';
            this._state.messagesPage.messages.push({id: 5, message: body});
            this._callSubscriber(this._state);
        }
    }

}

export let addPostActionCreator = () => {
    return {
        type: ADD_POST
    }
}

export let updateNewPostTextActionCreator = (text) => {
    return {
        type: UPDATE_NEW_POST_TEXT,
        newText: text
    }
}

export let sendMessageActionCreator = (text) => {
    return {
        type: SEND_MESSAGE
    }
}

export let updateNewMessageTextActionCreator = (text) => {
    return {
        type: UPDATE_NEW_MESSAGE_TEXT,
        newText: text
    }
}

export default store;
window.store = store;
