import React from "react";
import hacker from "../../media/hacker.jpg"
import classes from "./Users.module.css"
import {NavLink} from "react-router-dom";
import * as axios from "axios";
import {usersAPI} from "../../api/api";

let Users = (props) => {


    let pagesCount =Math.ceil(props.totalUsersCount/props.pageSize);
    let pages = [];
    for(let i=1;i<=pagesCount;i++){
        pages.push(i)
    }

    return (
        <div>
            <div>
                {pages.map((page => {
                    return (
                        <span className={(props.currentPage === page) ? classes.selectedPage : classes.page}
                              onClick={(e) => props.onPageChanged(page)}>{page}</span>
                    )
                }))}

            </div>
            {
                props.users.map((user) => {
                    return (
                        <div key={user.id}>
                            <span>
                                <div>
                                    <NavLink to={`/profile/${user.id}`}>
                                        <img src={user.photos.small != null ? user.photos.small : hacker}
                                             className={classes.userIcon}/>
                                    </NavLink>
                                </div>
                                <div>
                                    {user.followed ?
                                        <button onClick={() => (

                                            axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${user.id}`,{
                                                withCredentials: true,
                                                headers: {
                                                    "API-KEY": "858c15c3-c7f7-4ff2-8f05-b2146fd62275"
                                                }
                                            }).then(response => {
                                            if (response.data.resultCode === 0) {
                                                props.unfollow(user.id)
                                            }
                                        })

                                        )}>UNFollow</button>
                                        : <button onClick={() => (
                                            usersAPI.followUsers(user.id).then(data => {
                                            if (data.resultCode === 0) {
                                            props.follow(user.id)
                                        }
                                        }))}>Follow</button>}

                                </div>
                            </span>
                            <span>
                                <span>
                                    <div>{user.name}</div>
                                    <div>{user.status}</div>
                                </span>
                                <span>
                                    <div>{'user.location.city'}</div>
                                    <div>{'user.location.country'}</div>
                                </span>
                            </span>
                        </div>
                    )
                })
            }
        </div>
    )

}

export default Users;
