import React from "react";

let store = {

    _state :{
        profilePage: {
            posts: [
                {id: 1, message: "Hi,from Java man", likesCount: 25},
                {id: 1, message: "Python the best_ ho-ho-ho", likesCount: 55}
            ],
            newPostText: ""
        },
        messagesPage: {
            dialogs: [
                {id: 1, name: "Roman"},
                {id: 2, name: "Daniel"},
                {id: 3, name: "Max"}],
            messages: [
                {id: 1, message: "Hi"},
                {id: 2, message: "When will u connect Mongo Homie???)))"},
                {id: 3, message: "AHAHAHAHhHH"}]
        },
        sidebar: {
            friends: [
                {id: 1, name: "Max"},
                {id: 2, name: "Daniel"}
            ]
        }
    },

    rerenderEntireTree(){
        console.log("state is changed")
    },

    getState(){
         return(this._state)
    },

    addPost(postMessage){
        let newPost = {
            id: 5,
            message: this.getState().profilePage.newPostText,
            likesCount: 100
        };
        this.getState().profilePage.posts.push(newPost);
        this.getState().profilePage.newPostText = "";
        this.rerenderEntireTree(this.getState());
    },

    updateNewPostText(newText){
        this.getState().profilePage.newPostText = newText;
        this.rerenderEntireTree(this.getState());
    },

    subscribe(observer){
        this.rerenderEntireTree = observer; // observer - наблюдатель (pattern)
        //examples: addEventListener, onClick
    }
}
export default store;
