import * as axios from "axios";

const instance = axios.create({
    withCredentials:true,
    baseURL: "https://social-network.samuraijs.com/api/1.0/",
    headers: {
        "API-KEY": "3cbfbc66-8a78-42a7-be97-5b5476fb15b6"
    }
});

//GET,DELETE => Запросы без нагрузки(without payload), тоесть они не чего не передают на сервер (без body)
//POST,PUT => Запросы с нагрузкой(with payload), передают на сервер какой-то body

export const usersAPI = {
     getUsers(currentPage = 2,pageSize = 10){
        return (
            instance.get(`users?page=${currentPage}&count=${pageSize}`)
                .then(response => {
                    return response.data
                })

        )
    },
    followUsers(userId){
        return (
            instance.post(`follow/${userId}`,{}
            )
                .then(response => {
                    return response.data
                })

        )
    },
    UNfollowUsers(userId){
        return (
            instance.delete(`follow/${userId}`
            )
                .then(response => {
                    return response.data
                })

        )
    },
    authoriseMe(){
         return(
             instance.get(`auth/me`)
                 .then(response => {
                     return response.data
                 })
         )
    },
    getProfile(userId){
         return(
             axios.get(`https://social-network.samuraijs.com/api/1.0/profile/${userId}`)
         )
    },
    getStatus(userId){
         return(
             instance.get(`/profile/status/${userId}`)
         )
    },
    updateStatus(status){
         return(
             instance.put(`status/`, {status: status})
         )
    }
}
