import React from "react";
import Dialogs from "./Dialogs";
import {sendMessageActionCreator, updateNewMessageTextActionCreator} from "../../redux/dialogsReducer";
import {connect} from "react-redux";
import {Redirect} from "react-router-dom";
import {WithAuthRedirect} from "../../hoc/withAuthRedirect";

let mapStateToProps = (state) => {
    return {
        messagesPage: state.messagesPage,
        isAuth: state.auth.isAuth
    }

}
let mapDispatchToProps = (dispatch) => {
    return {
        updateNewMessageText: (text) => {
            dispatch(updateNewMessageTextActionCreator(text));
        },
        sendMessage: () => {
            dispatch(sendMessageActionCreator());
        }
    }
}

let DialogRedirectComponent = WithAuthRedirect(Dialogs)


//connect -> mapStateToProps - положит в себя state
//connect -> mapDispatchToProps - положит в себя store.dispatch.bind(store)
const DialogsContainer = connect(mapStateToProps,mapDispatchToProps)(DialogRedirectComponent);

export default DialogsContainer;
