import React from "react";
import hacker from "../../media/hacker.jpg";
import classes from "./Users.module.css";
import * as axios from "axios";

class UsersClass extends React.Component{

    onPageChanged = (event) => {
        this.props.setCurrnetPage
    }

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        });
    }

    render() {

        let pagesCount =Math.ceil(this.props.totalUsersCount/this.props.pageSize);
        let pages = [];
        for(let i=1;i<=pagesCount;i++){
            pages.push(i)
        }

        return (
            <div>
                <div>
                    {pages.map((page => {
                        return(
                            <span className={this.props.currentPage === page && classes.selectedPage}
                            onClick={this.props.setCurrentPage(page)}>{page}</span>
                        )
                    }))}

                </div>
                {
                    this.props.users.map((user) => {
                        return (
                            <div key={user.id}>
                            <span>
                                <div>
                                    <img src={user.photos.small != null ? user.photos.small : hacker} className={classes.userIcon}/>
                                </div>
                                <div>
                                    {user.followed ? <button onClick={() => this.props.unfollow(user.id)}>UNFollow</button>
                                        : <button onClick={() => this.props.follow(user.id)}>Follow</button>}

                                </div>
                            </span>
                                <span>
                                <span>
                                    <div>{user.name}</div>
                                    <div>{user.status}</div>
                                </span>
                                <span>
                                    <div>{'user.location.city'}</div>
                                    <div>{'user.location.country'}</div>
                                </span>
                            </span>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

export default UsersClass
