import React from "react";
import Header from "./Header";
import * as axios from "axios";
import {connect} from "react-redux";
import {setUserDataActionCreator} from "../../redux/authReducer";
import {usersAPI} from "../../api/api";

class HeaderContainer extends React.Component{

    componentDidMount() {
        usersAPI.authoriseMe().then(data => {
                if(data.resultCode === 0) {
                    let {id, login, email} = data.data;
                    this.props.setUserDataActionCreator(id,login,email);
                }
        });
    }

    render() {

        return(
            <Header {...this.props}/>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        isAuth: state.auth.isAuth,
        login: state.auth.login
    }
}

export default connect(mapStateToProps, {setUserDataActionCreator})(HeaderContainer);
