import React from "react";
import classes from "./Dialogs.module.css";
import {NavLink} from "react-router-dom";

const DialogItem = (props) => {
    let path = "/dialogs/" + props.id;

    return (
        <NavLink to={path} className={classes.dialog_item + ' ' + classes.active}>
            {props.name}
        </NavLink>
    )
}

const Message = (props) => {
    return (
        <div className={classes.message_item}>
            {props.message}
        </div>
    )
}

const Dialogs = (props) => {

    let dialogsData = [
        {id: 1, name: "Roman"},
        {id: 2, name: "Daniel"},
        {id: 3, name: "Max"}]

    let dialogsElements = dialogsData.map((dialog) =>
            <DialogItem name={dialog.name} id={dialog.id}/>
        );

    let messageElements = messagesData.map((message) =>
        <Message message={message.message} id={message.id}/>
    )

    let messagesData = [
        {id: 1, message: "Hi"},
        {id: 2, message: "When will u connect Mongo Homie???)))"},
        {id: 3, message: "AHAHAHAHhHH"}]

    return(
        <div className={classes.dialogs}>
            <div className={classes.dialogs_items}>
                {dialogsElements}
            </div>
            <div className={classes.messages}>
                <Message message={messagesData[0].message} id={messagesData[0].id}/>
                <Message message={messagesData[1].message} id={messagesData[1].id}/>
                <Message message={messagesData[2].message} id={messagesData[2].id}/>
            </div>
        </div>
    )
}

export default Dialogs;
