import React from "react";
import classes from "./MyPosts.module.css";
import Post from "./Post/Post";
import {reduxForm,Field} from "redux-form";

const PostForm = (props) => {
    return (
        <form>
            <Field component={"textarea"} name={"textarea"} onChange={props.onPostChange} ref={props.ref} value={props.value}/>
            <button onClick={props.addPost}>Add Post</button>
            <button>Remove</button>
        </form>
    )
}

const ReduxPostForm = reduxForm({
    form: "postForm"
})(PostForm)

const MyPosts = (props) => {

    let postElements = props.posts.map((post) =>
        <Post message={post.message} likeCounts={post.likesCount}/>
    )

    let newPostElement = React.createRef();

    let addPost = () => {
        props.addPost();
    }

    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.updateNewPostText(text);
    }


    return (
        <div>
            {postElements}
            {/*<div>*/}
            {/*    <textarea onChange={onPostChange} ref={newPostElement} value={props.newPostText}/>*/}
            {/*    <button onClick={addPost}>Add post</button>*/}
            {/*    <button>Remove</button>*/}
            {/*</div>*/}
            <ReduxPostForm {...props} addPost={addPost} onPostChange={onPostChange} value={props.newPostText}
            ref={newPostElement}/>
            <div className={classes.posts}>
             </div>
        </div>
    )
}

export default MyPosts;
