import React from "react";
import back_img from "../../../media/back_img.jpg";
import raccoon from "../../../media/raccoon.jpg";
import Loader from "../../Loader/Loader";

const ProfileInfo = (props) => {
    if(!props.profile){
        return <Loader/>
    }
    let contacts = props.profile.contacts.map((item) => {
        return(
                <li>{item}</li>
        )
    })

    return (
    <div>
        <div>
            <img src={props.profile.photos.large}/>
        </div>
        <div>
            <div>About me: {props.profile.aboutMe}</div>
        </div>
        <div>
            <div>Job description: {props.profile.lookingForAJobDescription}</div>
        </div>
        <div>
            <ul>Contacts: {contacts}</ul>
        </div>
    </div>
    )
}

export default ProfileInfo;
