import React from "react";
import hacker from "../../media/hacker.jpg"
import classes from "./Users.module.css"

let Users = (props) => {

    if (props.users.length)
    props.setUsers([{id: 1,icon:hacker, followed:true ,fullName: "Serg", status: "programmer_without_work(yet)", location:{city: "London", country: "GB"}},
        {id: 2,icon:hacker, followed:true ,fullName: "Daniel", status: "scripi...man)))", location:{city: "San-Diego", country: "USA"}},
        {id: 3,icon:hacker, followed: false ,fullName: "Max", status: "i'm wanna be fit", location:{city: "Copenhagen", country: "Denmark"}}
    ]);

    return (
        <div>
            {
                props.users.map((user) => {
                    return (
                        <div key={user.id}>
                            <span>
                                <div>
                                    <img src={user.icon} className={classes.userIcon}/>
                                </div>
                                <div>
                                    {user.followed ? <button onClick={() => props.unfollow(user.id)}>UNFollow</button>
                                        : <button onClick={() => props.follow(user.id)}>Follow</button>}

                                </div>
                            </span>
                            <span>
                                <span>
                                    <div>{user.fullName}</div>
                                    <div>{user.status}</div>
                                </span>
                                <span>
                                    <div>{user.location.city}</div>
                                    <div>{user.location.country}</div>
                                </span>
                            </span>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Users;
