import React from "react";
import Profile from "./Profile";
import {connect} from "react-redux";
import {setUserProfileActionCreator, setUserProfileThunkCreator} from "../../redux/profileReducer";
import {withRouter} from "react-router-dom";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.match.params.userId
        if (!userId){
            userId = 2;
        }
        setUserProfileThunkCreator(userId);
    }

    render() {
        return (
            <Profile {...this.props}/>
        )
    }
}

let mapStateToProps = (state) => ({
    profile: state.profilePage.profile
})

let WithUrlDataContainerComponent = withRouter(ProfileContainer);

export default connect(mapStateToProps,{setUserProfileActionCreator, setUserProfileThunkCreator})(WithUrlDataContainerComponent);
